    } else {
      adminConfig.isFirstLogin = true;
      console.log('‚ö†Ô∏è Admin config loaded - ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡∏ï‡∏±‡πâ‡∏á‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô');
