}

const adminSessions = new Set();

// System monitoring data
let systemStats = {
  connectedUsers: 0,
  activeProcesses: 0,
  totalRequests: 0,
  uptime: 0,
  startTime: Date.now()
};

// Advanced Analytics System
let analyticsData = {
  totalUsersToday: 0,
  totalDownloads: 0,
  avgProcessTime: 0,
  systemLoad: 0,
  hourlyUsage: new Array(24).fill(0),
  textureTypes: { youtube: 0, tiktok: 0, upload: 0 },
  userSessions: new Map(),
  activeUsers: new Map(),
  processingQueue: [],
  systemPerformance: {
    cpu: 0,
    memory: 0,
    disk: 0,
    networkUp: 0,
    networkDown: 0
  },
  recentActivity: [],
  processList: []
};

// Rate limiting system
let rateLimitData = {
  ipRequests: new Map(), // IP -> { count, lastReset }
  blockedIPs: new Set(),
  maxRequestsPerHour: 50,
  totalRequestsThisHour: 0
};

// Queue management system
