    Object.assign(bgCommon, defs);
    fs.writeFileSync(path.join(betmcCommonPath, 'betmc_bg_common.json'), JSON.stringify(bgCommon, null, 2));

    const uiDefs = { ui_defs: [`betmc_ui/betmc_common/${namespace}.json`] };
    fs.writeFileSync(path.join(uiPath, '_ui_defs.json'), JSON.stringify(uiDefs, null, 2));

    sendProgress(sessionId, 13, 90, '‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÑ‡∏ü‡∏•‡πå ZIP...');
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á zip
    const zipPath = path.join('zips', `${namespace}.zip`);
    fs.mkdirSync('zips', { recursive: true });
    const output = fs.createWriteStream(zipPath);
    const archive = archiver('zip');
    archive.pipe(output);
    archive.directory(outputDir, false);
    await archive.finalize();

    sendProgress(sessionId, 14, 100, '‡πÄ‡∏™‡∏£‡πá‡∏à‡∏™‡∏¥‡πâ‡∏ô!', 0);

    // ‡∏™‡πà‡∏á‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÅ‡∏à‡πâ‡∏á‡πÄ‡∏ï‡∏∑‡∏≠‡∏ô (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)
    if (userEmail) {
      const downloadUrl = `${req.protocol}://${req.get('host')}/${zipPath}`;
      await sendNotificationEmail(userEmail, textureName, downloadUrl);
    }

    // ‡∏™‡πà‡∏á‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏™‡∏∏‡∏î‡∏ó‡πâ‡∏≤‡∏¢
    setTimeout(() => {
      const client = progressClients.get(sessionId);
      if (client) {
        client.write(`data: ${JSON.stringify({ completed: true, zip: `/${zipPath}` })}\n\n`);
        client.end();
        progressClients.delete(sessionId);
      }
    }, 1000);

  } catch (err) {
    console.error('Error:', err);
    sendProgress(sessionId, 0, 0, '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î: ' + err.message);
    const client = progressClients.get(sessionId);
    if (client) {
      client.write(`data: ${JSON.stringify({ error: 'Error processing files' })}\n\n`);
      client.end();
      progressClients.delete(sessionId);
    }
  }
});

app.listen(3000, () => {
  console.log('Server listening at http://localhost:3000');
});

public/admin.css.content:
:root {
  --primary-color: #667eea;
  --secondary-color: #764ba2;
  --accent-color: #f093fb;
  --success-color: #4CAF50;
  --error-color: #f44336;
  --warning-color: #ff9800;
  --glass-bg: rgba(255, 255, 255, 0.1);
  --glass-border: rgba(255, 255, 255, 0.2);
  --text-light: #ffffff;
  --text-dark: #333333;
  --shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  --border-radius: 16px;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Kanit', 'Segoe UI', sans-serif;
  min-height: 100vh;
  background: #000;
  color: var(--text-light);
  line-height: 1.6;
}

/* Background */
.bg-blur {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, var(--primary-color), var(--secondary-color), var(--accent-color), #4facfe);
  background-size: 400% 400%;
  animation: gradientShift 15s ease infinite;
  filter: blur(20px);
  z-index: -2;
}

@keyframes gradientShift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

.bg-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  z-index: -1;
}

/* Login Screen */
.login-screen {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  padding: 20px;
}

.login-container {
  background: var(--glass-bg);
  backdrop-filter: blur(20px);
  border-radius: var(--border-radius);
  padding: 40px;
  border: 1px solid var(--glass-border);
  box-shadow: var(--shadow);
  width: 100%;
  max-width: 400px;
  animation: slideUp 0.8s ease-out;
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(50px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.login-header {
  text-align: center;
  margin-bottom: 30px;
}

.login-header i {
  font-size: 3rem;
  color: var(--primary-color);
  margin-bottom: 15px;
  display: block;
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); }
}

.login-header h2 {
  color: var(--text-light);
  margin-bottom: 10px;
  font-size: 1.8rem;
}

.login-header p {
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.9rem;
}

.form-group {
  margin-bottom: 20px;
}

.form-group label {
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 8px;
  font-weight: 500;
  color: var(--text-light);
}

.form-group input {
  width: 100%;
  padding: 15px;
  border: 2px solid var(--glass-border);
  border-radius: 10px;
  background: rgba(255, 255, 255, 0.9);
  color: var(--text-dark);
  font-size: 1rem;
  transition: all 0.3s ease;
}

.form-group input:focus {
  outline: none;
  border-color: var(--primary-color);
  box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.2);
}

.login-btn {
  width: 100%;
  padding: 15px;
  background: linear-gradient(45deg, var(--primary-color), var(--secondary-color));
  color: white;
  border: none;
  border-radius: 10px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
}

.login-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
}

.back-to-main {
  text-align: center;
  margin-top: 20px;
}

.back-link {
  color: rgba(255, 255, 255, 0.7);
  text-decoration: none;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  transition: color 0.3s ease;
}

.back-link:hover {
  color: var(--accent-color);
}

/* Admin Panel */
.admin-panel {
  max-width: 1400px;
  margin: 0 auto;
  padding: 20px;
}

.admin-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 40px;
  padding: 30px;
  background: var(--glass-bg);
  backdrop-filter: blur(20px);
  border-radius: var(--border-radius);
  border: 1px solid var(--glass-border);
  box-shadow: var(--shadow);
}

.admin-title h1 {
  font-size: 2.5rem;
  background: linear-gradient(45deg, var(--primary-color), var(--accent-color));
  background-clip: text;
  -webkit-background-clip: text;
  color: transparent;
  margin-bottom: 5px;
}

.admin-title p {
  color: rgba(255, 255, 255, 0.7);
  font-size: 1.1rem;
}

.logout-btn {
  background: linear-gradient(45deg, var(--error-color), #e57373);
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 10px;
  cursor: pointer;
  font-weight: 600;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
}

.logout-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(244, 67, 54, 0.3);
}

/* Control Grid */
.control-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 25px;
}

.control-card {
  background: var(--glass-bg);
  backdrop-filter: blur(20px);
  border-radius: var(--border-radius);
  border: 1px solid var(--glass-border);
  box-shadow: var(--shadow);
  overflow: hidden;
  transition: all 0.3s ease;
}

.control-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.4);
}

.control-card.full-width {
  grid-column: 1 / -1;
}

.card-header {
  padding: 20px;
  background: rgba(0, 0, 0, 0.2);
  border-bottom: 1px solid var(--glass-border);
}

.card-header h3 {
  color: var(--text-light);
  font-size: 1.3rem;
  display: flex;
  align-items: center;
  gap: 10px;
}

.card-header i {
  color: var(--accent-color);
}

.card-content {
  padding: 25px;
}

/* Form Elements */
.form-group {
  margin-bottom: 20px;
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: var(--text-light);
}

.form-group input,
.form-group textarea,
.form-group select {
  width: 100%;
  padding: 12px;
  border: 2px solid var(--glass-border);
  border-radius: 8px;
  background: rgba(255, 255, 255, 0.9);
  color: var(--text-dark);
  font-size: 1rem;
  transition: all 0.3s ease;
  font-family: inherit;
}

.form-group input:focus,
.form-group textarea:focus,
.form-group select:focus {
  outline: none;
  border-color: var(--primary-color);
  box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.2);
}

.form-group input[type="color"] {
  height: 50px;
  padding: 5px;
  cursor: pointer;
}

.form-group textarea {
  resize: vertical;
  min-height: 100px;
}

/* Color Grid */
.color-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 15px;
  margin-bottom: 20px;
}

/* Toggle Switches */
.toggle-group {
  margin-bottom: 25px;
}

.toggle-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
  padding: 15px;
  background: rgba(0, 0, 0, 0.2);
  border-radius: 10px;
}

.toggle-item label {
  color: var(--text-light);
  font-weight: 500;
  margin-bottom: 0;
}

.toggle-switch {
  position: relative;
  width: 60px;
  height: 30px;
}

.toggle-switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.toggle-slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(255, 255, 255, 0.2);
  transition: 0.3s;
  border-radius: 30px;
}

.toggle-slider:before {
  position: absolute;
  content: "";
  height: 22px;
  width: 22px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  transition: 0.3s;
  border-radius: 50%;
}

input:checked + .toggle-slider {
  background: linear-gradient(45deg, var(--primary-color), var(--accent-color));
}

input:checked + .toggle-slider:before {
  transform: translateX(30px);
}

/* System Status */
.system-status {
  margin-top: 20px;
}

.status-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
  padding: 10px;
  background: rgba(0, 0, 0, 0.1);
  border-radius: 8px;
}

.status-label {
  color: var(--text-light);
  font-weight: 500;
}

.status-value {
  padding: 4px 12px;
  border-radius: 15px;
  font-size: 0.85rem;
  font-weight: 600;
}

.status-value.online {
  background: var(--success-color);
  color: white;
}

.status-value.offline {
  background: var(--error-color);
  color: white;
}

/* Preview Container */
.preview-container {
  margin: 15px 0;
  text-align: center;
}

.preview-container img {
  max-width: 100%;
  height: auto;
  border-radius: 8px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
}

/* Announcement Actions */
.announcement-actions {
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
}

.announcement-preview {
  padding: 15px;
  background: rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  border: 1px solid var(--glass-border);
}

.preview-label {
  font-size: 0.85rem;
  color: rgba(255, 255, 255, 0.7);
  margin-bottom: 8px;
}

.preview-content {
  color: var(--text-light);
  font-style: italic;
}

/* File Management */
.file-stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 15px;
  margin-bottom: 20px;
}

.file-actions {
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
  flex-wrap: wrap;
}

.file-details {
  margin-top: 20px;
}

.detail-section h4 {
  color: var(--accent-color);
  margin-bottom: 10px;
  font-size: 1.1rem;
}

.activity-list {
  max-height: 200px;
  overflow-y: auto;
  background: rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  padding: 10px;
}

.activity-item {
  padding: 8px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  font-size: 0.9rem;
}

.activity-item:last-child {
  border-bottom: none;
}

.activity-time {
  color: var(--accent-color);
  font-weight: 500;
  margin-right: 10px;
}

.activity-message {
  opacity: 0.9;
}

/* System Actions */
.system-actions {
  margin-top: 15px;
  display: flex;
  gap: 10px;
}

/* Error Logs */
.error-controls {
  display: flex;
  gap: 10px;
  margin-bottom: 15px;
}

.error-logs {
  margin-top: 15px;
}

.logs-container {
  max-height: 300px;
  overflow-y: auto;
  background: rgba(0, 0, 0, 0.3);
  border-radius: 8px;
  padding: 15px;
}

.log-item {
  padding: 10px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  font-family: 'Courier New', monospace;
  font-size: 0.85rem;
  display: flex;
  align-items: center;
  gap: 10px;
}

.log-item:last-child {
  border-bottom: none;
}

.log-item.error {
  border-left: 3px solid var(--error-color);
  padding-left: 10px;
}

.log-item.warning {
  border-left: 3px solid var(--warning-color);
  padding-left: 10px;
}

.log-item.info {
  border-left: 3px solid var(--success-color);
  padding-left: 10px;
}

.log-time {
  color: var(--accent-color);
  font-weight: 500;
  min-width: 80px;
}

.log-level {
  background: rgba(255, 255, 255, 0.1);
  padding: 2px 6px;
  border-radius: 4px;
  font-size: 0.75rem;
  min-width: 60px;
  text-align: center;
}

.log-message {
  flex: 1;
  opacity: 0.9;
}

/* Statistics Grid */
.stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
  gap: 15px;
}

.stat-item {
  text-align: center;
  padding: 20px;
  background: rgba(0, 0, 0, 0.2);
  border-radius: 12px;
  border: 1px solid var(--glass-border);
}

.stat-value {
  font-size: 2rem;
  font-weight: 700;
  color: var(--accent-color);
  margin-bottom: 5px;
  animation: countUp 0.5s ease-out;
}

@keyframes countUp {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}

.stat-label {
  font-size: 0.85rem;
  color: rgba(255, 255, 255, 0.7);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Buttons */
.btn {
  padding: 12px 20px;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-family: inherit;
  font-size: 0.95rem;
}

.btn-primary {
  background: linear-gradient(45deg, var(--primary-color), var(--secondary-color));
  color: white;
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
}

.btn-secondary {
  background: rgba(255, 255, 255, 0.1);
  color: var(--text-light);
  border: 1px solid var(--glass-border);
}

.btn-secondary:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: translateY(-2px);
}

.btn-warning {
  background: linear-gradient(45deg, var(--warning-color), #ffb74d);
  color: white;
}

.btn-warning:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(255, 152, 0, 0.3);
}

/* Toast Notifications */
.toast-container {
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 10000;
}

.toast {
  background: var(--glass-bg);
  backdrop-filter: blur(20px);
  color: var(--text-light);
  padding: 15px 20px;
  border-radius: 12px;
  border: 1px solid var(--glass-border);
  margin-bottom: 10px;
  animation: toastSlideIn 0.3s ease;
  box-shadow: var(--shadow);
  display: flex;
  align-items: center;
  gap: 10px;
  min-width: 300px;
}

.toast.success {
  border-color: var(--success-color);
  background: linear-gradient(135deg, rgba(76, 175, 80, 0.2), var(--glass-bg));
}

.toast.error {
  border-color: var(--error-color);
  background: linear-gradient(135deg, rgba(244, 67, 54, 0.2), var(--glass-bg));
}

.toast.warning {
  border-color: var(--warning-color);
  background: linear-gradient(135deg, rgba(255, 152, 0, 0.2), var(--glass-bg));
}

/* Admin Mode Badge */
.admin-mode-badge {
  position: fixed;
  top: 20px;
  left: 20px;
  z-index: 10000;
  background: linear-gradient(135deg, #ff6b6b, #ff8e53, #ff6b9d);
  color: white;
  padding: 12px 20px;
  border-radius: 25px;
  box-shadow: 0 8px 32px rgba(255, 107, 107, 0.4);
  border: 2px solid rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(10px);
  animation: adminBadgeFloat 3s ease-in-out infinite, adminGlow 2s ease-in-out infinite alternate;
}

.badge-content {
  display: flex;
  align-items: center;
  gap: 10px;
  position: relative;
}

.admin-text {
  font-weight: 700;
  font-size: 14px;
  letter-spacing: 2px;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.8);
}

.badge-content i {
  filter: drop-shadow(0 0 8px #4CAF50);
}

.pulse-ring {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  border: 3px solid rgba(255, 255, 255, 0.6);
  border-radius: 25px;
  animation: pulseRing 2s ease-out infinite;
}

/* Admin Status Indicator */
.admin-status-indicator {
  display: flex;
  align-items: center;
  gap: 8px;
  color: #4CAF50;
  font-size: 14px;
  margin-top: 10px;
  animation: statusPulse 2s ease-in-out infinite;
}

.admin-status-indicator i {
  animation: shieldGlow 3s ease-in-out infinite alternate;
}



/* Animations */
@keyframes adminBadgeFloat {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-5px); }
}

@keyframes adminGlow {
  0% {
    box-shadow: 0 8px 32px rgba(255, 107, 107, 0.4);
    border-color: rgba(255, 255, 255, 0.2);
  }
  100% {
    box-shadow: 0 8px 32px rgba(255, 107, 107, 0.8), 0 0 30px rgba(255, 107, 107, 0.6);
    border-color: rgba(255, 255, 255, 0.6);
  }
}



@keyframes pulseRing {
  0% {
    transform: translate(-50%, -50%) scale(0.8);
    opacity: 1;
  }
  100% {
    transform: translate(-50%, -50%) scale(1.3);
    opacity: 0;
  }
}

@keyframes statusPulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.7; }
}

@keyframes shieldGlow {
  0% {
    color: #4CAF50;
    text-shadow: 0 0 5px #4CAF50;
  }
  100% {
    color: #81C784;
    text-shadow: 0 0 15px #4CAF50, 0 0 25px #4CAF50;
  }
}



@keyframes toastSlideIn {
  from {
    opacity: 0;
    transform: translateX(100%);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

/* Chat Card Styles */
.chat-card .card-content {
  height: 400px;
  display: flex;
  flex-direction: column;
}

.chat-messages {
  flex: 1;
  overflow-y: auto;
  padding: 10px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  margin-bottom: 10px;
  max-height: 300px;
}

.chat-message {
  margin-bottom: 12px;
  padding: 8px 12px;
  border-radius: 12px;
  max-width: 80%;
  word-wrap: break-word;
}

.chat-message.admin {
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  margin-left: auto;
  text-align: right;
}

.chat-message.user {
  background: rgba(255, 255, 255, 0.1);
  color: #fff;
}

.chat-message .message-header {
  font-size: 11px;
  opacity: 0.7;
  margin-bottom: 4px;
}

.chat-message .message-content {
  font-size: 14px;
  line-height: 1.4;
}

.chat-message.file {
  background: rgba(102, 126, 234, 0.2);
  border: 1px solid rgba(102, 126, 234, 0.4);
}

.chat-file-preview {
  margin-top: 8px;
  padding: 8px;
  background: rgba(0, 0, 0, 0.3);
  border-radius: 8px;
}

