function showNewEventNotification(event) {
  showToast(`üéâ ‡∏≠‡∏µ‡πÄ‡∏ß‡πâ‡∏ô‡∏ó‡πå‡πÉ‡∏´‡∏°‡πà: ${event.title}`, 'success');
}

// Add additional CSS animations
const adminEffectStyles = document.createElement('style');
adminEffectStyles.textContent = `
  @keyframes alertFlash {
    0%, 100% { background: rgba(255, 0, 0, 0.8); }
    50% { background: rgba(255, 255, 0, 0.8); }
  }

  @keyframes screenShake {
    0%, 100% { transform: translateX(0); }
    10%, 30%, 50%, 70%, 90% { transform: translateX(-2px); }
    20%, 40%, 60%, 80% { transform: translateX(2px); }
  }

  @keyframes epicEntry {
    0% { transform: scale(0) rotate(180deg); opacity: 0; }
    50% { transform: scale(1.2) rotate(0deg); opacity: 1; }
    100% { transform: scale(1) rotate(0deg); opacity: 1; }
  }

  @keyframes epicGlow {
    0% { box-shadow: 0 0 50px rgba(255, 0, 0, 0.8); }
    100% { box-shadow: 0 0 100px rgba(255, 255, 0, 1); }
  }

  @keyframes fadeOut {
    to { opacity: 0; transform: scale(0.8); }
  }

  @keyframes alertPulse {
    0%, 100% { transform: translate(-50%, -50%) scale(1); }
    50% { transform: translate(-50%, -50%) scale(1.05); }
  }

  @keyframes rainbowBackground {
    0% { filter: hue-rotate(0deg); }
    100% { filter: hue-rotate(360deg); }
  }

  @keyframes confettiFall {
    to { transform: translateY(100vh) rotate(360deg); }
  }

  @keyframes matrixFall {
    to { transform: translateY(100vh); }
  }

  @keyframes rainbowShift {
    0% { filter: hue-rotate(0deg); }
    100% { filter: hue-rotate(360deg); }
  }

  @keyframes epicScale {
    0% { transform: translate(-50%, -50%) scale(1); }
    100% { transform: translate(-50%, -50%) scale(1.1); }
  }

  @keyframes speedPulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
  }
`;
document.head.appendChild(adminEffectStyles);

// Add toast slide out animation
const style = document.createElement('style');
style.textContent = `
  @keyframes toastSlideOut {
    from {
      opacity: 1;
      transform: translateX(0);
    }
    to {
      opacity: 0;
      transform: translateX(100%);
    }
  }
`;
document.head.appendChild(style);

// Chat Widget Functions
function initializeChatWidget() {
  const chatWidget = document.getElementById('chatWidget');
  const toggleChat = document.getElementById('toggleChat');
  const mainChatInput = document.getElementById('mainChatInput');
  const mainChatSend = document.getElementById('mainChatSend');
  const mainChatMessages = document.getElementById('mainChatMessages');

  // Toggle chat widget
  toggleChat.addEventListener('click', () => {
    chatWidget.classList.toggle('collapsed');
    const icon = toggleChat.querySelector('i');
    if (chatWidget.classList.contains('collapsed')) {
      icon.className = 'fas fa-chevron-up';
    } else {
      icon.className = 'fas fa-chevron-down';
    }
  });

  // Send message on Enter key
  mainChatInput.addEventListener('keypress', (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMainChatMessage();
    }
  });

  // Send button click
  mainChatSend.addEventListener('click', sendMainChatMessage);

  // Socket events for chat
  socket.on('chat-message', (data) => {
    displayMainChatMessage(data);
  });

  socket.on('admin-chat-message', (data) => {
    displayMainChatMessage(data);
  });
}

function sendMainChatMessage() {
  const mainChatInput = document.getElementById('mainChatInput');
  const message = mainChatInput.value.trim();

  if (!message) return;

  const messageData = {
    type: 'text',
    content: message,
    sender: 'user',
    timestamp: Date.now()
  };

  socket.emit('chat-message', messageData);
  mainChatInput.value = '';

  // Display locally
  displayMainChatMessage(messageData);
}

function displayMainChatMessage(messageData) {
  const mainChatMessages = document.getElementById('mainChatMessages');
  const messageElement = document.createElement('div');
  messageElement.className = `chat-message ${messageData.sender}`;

  const time = new Date(messageData.timestamp).toLocaleTimeString('th-TH', {
    hour: '2-digit',
    minute: '2-digit'
  });

  let messageContent = '';

  if (messageData.type === 'file') {
    const isImage = messageData.fileType && messageData.fileType.startsWith('image/');
    messageContent = `
      <div class="message-header">${messageData.sender} ‚Ä¢ ${time}</div>
      <div class="message-content">
        üìé ${messageData.content}
        ${isImage && messageData.fileData ? `<div class="chat-file-preview"><img src="${messageData.fileData}" alt="Uploaded file"></div>` : ''}
      </div>
    `;
  } else {
    messageContent = `
      <div class="message-header">${messageData.sender} ‚Ä¢ ${time}</div>
      <div class="message-content">${messageData.content}</div>
    `;
  }

  messageElement.innerHTML = messageContent;
  mainChatMessages.appendChild(messageElement);
  mainChatMessages.scrollTop = mainChatMessages.scrollHeight;
}

// Media Player Functions
let backgroundVideo = null;
let backgroundAudio = null;
let isMediaPlaying = false;

function initializeMediaPlayer() {
  const mediaPlayer = document.getElementById('mediaPlayer');
  const backgroundVideo = document.getElementById('backgroundVideo');
  const backgroundAudio = document.getElementById('backgroundAudio');
  const mediaToggle = document.getElementById('mediaToggle');

  // Media control events from admin
  socket.on('media-play', (mediaData) => {
    playBackgroundMedia(mediaData);
  });

  socket.on('media-pause', () => {
    pauseBackgroundMedia();
  });

  socket.on('media-stop', () => {
    stopBackgroundMedia();
  });

  socket.on('media-volume', (data) => {
    setMediaVolume(data.volume);
  });

  // Media toggle button
  mediaToggle.addEventListener('click', () => {
    if (isMediaPlaying) {
      pauseBackgroundMedia();
    } else {
      resumeBackgroundMedia();
    }
  });
}

function playBackgroundMedia(mediaData) {
  const mediaPlayer = document.getElementById('mediaPlayer');
  const backgroundVideo = document.getElementById('backgroundVideo');
  const backgroundAudio = document.getElementById('backgroundAudio');
  const mediaToggle = document.getElementById('mediaToggle');

  // Stop any current media
  stopBackgroundMedia();

  // Show media player
  mediaPlayer.style.display = 'block';

  if (mediaData.type === 'video') {
    // Play as video
    mediaPlayer.classList.add('video-mode');
    backgroundVideo.style.display = 'block';
    backgroundAudio.style.display = 'none';

    // Try to load YouTube URL directly or use embed
    if (mediaData.url.includes('youtube.com') || mediaData.url.includes('youtu.be')) {
      const videoId = extractYouTubeVideoId(mediaData.url);
      if (videoId) {
        backgroundVideo.innerHTML = `
          <iframe 
            width="100%" 
            height="100%" 
            src="https://www.youtube.com/embed/${videoId}?autoplay=1&loop=1&mute=0&controls=0&showinfo=0" 
            frameborder="0" 
            allow="autoplay; encrypted-media" 
            allowfullscreen>
          </iframe>
        `;
      }
    } else {
      backgroundVideo.src = mediaData.url;
      backgroundVideo.volume = mediaData.volume || 0.5;
      backgroundVideo.play().catch(console.error);
    }
  } else {
    // Play as audio only
    mediaPlayer.classList.remove('video-mode');
    backgroundVideo.style.display = 'none';
    backgroundAudio.style.display = 'block';

    if (mediaData.url.includes('youtube.com') || mediaData.url.includes('youtu.be')) {
      // For YouTube audio, we'll need a different approach
      showToast('YouTube audio playback requires video mode', 'info');
      return;
    } else {
      backgroundAudio.src = mediaData.url;
      backgroundAudio.volume = mediaData.volume || 0.5;
      backgroundAudio.play().catch(console.error);
    }
  }

  isMediaPlaying = true;
  updateMediaToggleIcon(true);
}

function pauseBackgroundMedia() {
  const backgroundVideo = document.getElementById('backgroundVideo');
  const backgroundAudio = document.getElementById('backgroundAudio');

  if (backgroundVideo.style.display !== 'none') {
    backgroundVideo.pause();
  }
  if (backgroundAudio.style.display !== 'none') {
    backgroundAudio.pause();
  }

  isMediaPlaying = false;
  updateMediaToggleIcon(false);
}

function resumeBackgroundMedia() {
  const backgroundVideo = document.getElementById('backgroundVideo');
  const backgroundAudio = document.getElementById('backgroundAudio');

  if (backgroundVideo.style.display !== 'none') {
    backgroundVideo.play().catch(console.error);
  }
  if (backgroundAudio.style.display !== 'none') {
    backgroundAudio.play().catch(console.error);
  }

  isMediaPlaying = true;
  updateMediaToggleIcon(true);
}

function stopBackgroundMedia() {
  const mediaPlayer = document.getElementById('mediaPlayer');
  const backgroundVideo = document.getElementById('backgroundVideo');
  const backgroundAudio = document.getElementById('backgroundAudio');

  // Stop and reset video
  backgroundVideo.pause();
  backgroundVideo.src = '';
  backgroundVideo.innerHTML = '';

  // Stop and reset audio
  backgroundAudio.pause();
  backgroundAudio.src = '';

  // Hide media player
  mediaPlayer.style.display = 'none';
  mediaPlayer.classList.remove('video-mode');

  isMediaPlaying = false;
  updateMediaToggleIcon(false);
}

function setMediaVolume(volume) {
  const backgroundVideo = document.getElementById('backgroundVideo');
  const backgroundAudio = document.getElementById('backgroundAudio');

  backgroundVideo.volume = volume;
  backgroundAudio.volume = volume;
}

function updateMediaToggleIcon(isPlaying) {
  const mediaToggle = document.getElementById('mediaToggle');
  const icon = mediaToggle.querySelector('i');

  if (isPlaying) {
    icon.className = 'fas fa-volume-up';
  } else {
    icon.className = 'fas fa-volume-mute';
  }
}

function extractYouTubeVideoId(url) {
  const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
  const match = url.match(regExp);
  return (match && match[2].length === 11) ? match[2] : null;
}

public/styles.css.content:
:root {
  --primary-color: #667eea;
  --secondary-color: #764ba2;
  --accent-color: #f093fb;
  --success-color: #4CAF50;
  --error-color: #f44336;
  --warning-color: #ff9800;
  --glass-bg: rgba(255, 255, 255, 0.1);
  --glass-border: rgba(255, 255, 255, 0.2);
  --text-light: #ffffff;
  --text-dark: #333333;
  --shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  --border-radius: 16px;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Kanit', 'Segoe UI', sans-serif;
  min-height: 100vh;
  background: #000;
  overflow-x: hidden;
  position: relative;
  line-height: 1.6;
}

/* Particles Container */
#particles-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -3;
  pointer-events: none;
}

/* Enhanced Background */
.bg-blur {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, var(--primary-color), var(--secondary-color), var(--accent-color), #4facfe);
  background-size: 400% 400%;
  animation: gradientShift 15s ease infinite;
  filter: blur(20px);
  z-index: -2;
}

@keyframes gradientShift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

.bg-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.4);
  z-index: -1;
}

/* Announcement Banner */
.announcement-banner {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background: linear-gradient(135deg, var(--warning-color), #ff7043);
  color: white;
  padding: 15px 20px;
  z-index: 1000;
  animation: slideDown 0.5s ease-out;
  box-shadow: var(--shadow);
}

.announcement-content {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 15px;
  max-width: 1200px;
  margin: 0 auto;
}

.close-announcement {
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  padding: 5px 10px;
  border-radius: 50%;
  cursor: pointer;
  transition: all 0.3s ease;
}

.close-announcement:hover {
  background: rgba(255, 255, 255, 0.3);
  transform: scale(1.1);
}

@keyframes slideDown {
  from {
    transform: translateY(-100%);
  }
  to {
    transform: translateY(0);
  }
}

/* Admin Access Button */
.admin-access {
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 999;
}

.admin-btn {
  background: rgba(0, 0, 0, 0.5);
  border: 2px solid var(--glass-border);
  color: var(--text-light);
  padding: 12px;
  border-radius: 50%;
  cursor: pointer;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);
  font-size: 1.2rem;
}

.admin-btn:hover {
  background: var(--glass-bg);
  transform: scale(1.1) rotate(90deg);
  box-shadow: var(--shadow);
}

/* Modern Glass Container */
.container {
  max-width: 900px;
  margin: 20px auto;
  background: var(--glass-bg);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border-radius: var(--border-radius);
  padding: 30px;
  box-shadow: var(--shadow);
  border: 1px solid var(--glass-border);
  animation: slideUp 0.8s ease-out;
  color: var(--text-light);
  position: relative;
  overflow: hidden;
}

.container::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
  animation: shimmer 3s infinite;
}

@keyframes shimmer {
  0% { left: -100%; }
  100% { left: 100%; }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(50px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Header with Modern Typography */
.header {
  text-align: center;
  margin-bottom: 40px;
  position: relative;
}

.header h1 {
  font-size: clamp(2rem, 5vw, 3rem);
  font-weight: 700;
  background: linear-gradient(45deg, var(--primary-color), var(--secondary-color), var(--accent-color));
  background-size: 200%;
  background-clip: text;
  -webkit-background-clip: text;
  color: transparent;
  animation: textGlow 3s ease-in-out infinite;
  margin-bottom: 10px;
}

/* Glitch Effect */
.glitch-text {
  position: relative;
}

.glitch-text::before,
.glitch-text::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0;
}

.glitch-text::before {
  animation: glitch1 2s infinite;
  color: #ff0000;
  z-index: -1;
}

.glitch-text::after {
  animation: glitch2 2s infinite;
  color: #00ff00;
  z-index: -2;
}

@keyframes glitch1 {
  0%, 95% { opacity: 0; }
  96%, 97% { opacity: 0.1; transform: translate(2px, 0); }
  98%, 99% { opacity: 0.1; transform: translate(-2px, 0); }
  100% { opacity: 0; }
}

@keyframes glitch2 {
  0%, 90% { opacity: 0; }
  91%, 92% { opacity: 0.1; transform: translate(-2px, 0); }
  93%, 94% { opacity: 0.1; transform: translate(2px, 0); }
  95% { opacity: 0; }
}

@keyframes textGlow {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

.header p {
  color: rgba(255, 255, 255, 0.8);
  font-size: 1.1rem;
  font-weight: 300;
}

.version-badge {
  display: inline-block;
  background: linear-gradient(45deg, var(--primary-color), var(--secondary-color));
  color: white;
  padding: 6px 16px;
  border-radius: 20px;
  font-size: 0.85rem;
  font-weight: 500;
  margin-top: 10px;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.3);
}

/* Floating Animation */
.floating {
  animation: floating 3s ease-in-out infinite;
}

@keyframes floating {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

.floating-animation {
  animation: floating 4s ease-in-out infinite;
}

/* Enhanced GIF Section */
.gif-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 30px 0;
  position: relative;
}

.gif-wrapper img {
  max-width: 100%;
  height: auto;
  border-radius: var(--border-radius);
  box-shadow: 0 12px 40px rgba(0, 0, 0, 0.5);
  transition: transform 0.3s ease;
}

.gif-wrapper img:hover {
  transform: scale(1.02);
}

/* Modern Form Grid */
.form-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 25px;
  margin-bottom: 30px;
}

.form-group {
  display: flex;
  flex-direction: column;
  position: relative;
}

.form-group.full-width {
  grid-column: 1 / -1;
}

.form-group label {
  font-weight: 600;
  color: var(--text-light);
  margin-bottom: 12px;
  font-size: 1rem;
  display: flex;
  align-items: center;
