  animation: rotate 10s linear infinite;
}

@keyframes rotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.youtube-section h3 {
  color: #ff4444;
  margin-bottom: 15px;
  display: flex;
  align-items: center;
  gap: 10px;
  position: relative;
  z-index: 1;
}

.youtube-input-group {
  display: flex;
  gap: 15px;
  align-items: end;
  margin-bottom: 20px;
  position: relative;
  z-index: 1;
}

.youtube-input {
  flex: 1;
}

.youtube-btn, .tiktok-btn {
  background: linear-gradient(45deg, #ff0000, #ff4444);
  color: white;
  border: none;
  padding: 16px 24px;
  border-radius: 12px;
  cursor: pointer;
  font-weight: 600;
  transition: all 0.3s ease;
  white-space: nowrap;
  position: relative;
  overflow: hidden;
}

.youtube-btn::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s;
}

.youtube-btn:hover::before {
  left: 100%;
}

.youtube-btn:hover, .tiktok-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(255, 0, 0, 0.3);
}

.tiktok-btn {
  background: linear-gradient(45deg, #ff0050, #ff4081);
}

.tiktok-btn:hover {
  box-shadow: 0 8px 25px rgba(255, 0, 80, 0.3);
}

.youtube-btn:disabled, .tiktok-btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  transform: none;
}

.youtube-preview {
  margin-top: 20px;
  padding: 20px;
  background: var(--glass-bg);
  border-radius: 12px;
  border: 1px solid var(--glass-border);
  display: none;
  position: relative;
  z-index: 1;
}

.youtube-thumbnail {
  width: 100%;
  max-width: 320px;
  border-radius: 8px;
  margin-bottom: 15px;
}

.youtube-info h4 {
  color: var(--text-light);
  margin-bottom: 8px;
  font-size: 1.1rem;
}

.youtube-info p {
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.9rem;
}

.youtube-options {
  display: flex;
  gap: 20px;
  align-items: center;
  margin-top: 15px;
  flex-wrap: wrap;
  position: relative;
  z-index: 1;
}

.youtube-quality-select {
  display: flex;
  align-items: center;
  gap: 10px;
  color: var(--text-light);
}

.youtube-quality-select label {
  font-weight: 500;
  margin-bottom: 0;
}

.youtube-quality-select select {
  padding: 10px 15px;
  border: 2px solid var(--glass-border);
  border-radius: 8px;
  background: rgba(255, 255, 255, 0.9);
  color: var(--text-dark);
  font-family: inherit;
  font-size: 0.95rem;
}

.youtube-audio-checkbox {
  display: flex;
  align-items: center;
  gap: 8px;
  color: var(--text-light);
  white-space: nowrap;
}

.youtube-audio-checkbox input[type="checkbox"] {
  accent-color: var(--primary-color);
  transform: scale(1.2);
  cursor: pointer;
}

/* Modern Button */
.submit-btn {
  background: linear-gradient(45deg, var(--primary-color), var(--secondary-color));
  color: white;
  border: none;
  padding: 18px 36px;
  font-size: 1.1rem;
  font-weight: 600;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  width: 100%;
  margin-top: 30px;
  position: relative;
  overflow: hidden;
  font-family: inherit;
}

.pulse-animation {
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0% { box-shadow: 0 0 0 0 rgba(102, 126, 234, 0.7); }
  70% { box-shadow: 0 0 0 10px rgba(102, 126, 234, 0); }
  100% { box-shadow: 0 0 0 0 rgba(102, 126, 234, 0); }
}

.submit-btn:hover {
