}

@keyframes progressShine {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

.progress-text {
  text-align: center;
  margin-top: 10px;
  font-weight: 600;
  font-size: 1.1rem;
}

.progress-message {
  text-align: center;
  margin-bottom: 30px;
}

.progress-message p {
  color: rgba(255, 255, 255, 0.8);
  font-size: 1rem;
}

.progress-steps {
  display: flex;
