  padding: 15px;
}

.chat-widget .chat-messages {
  flex: 1;
  overflow-y: auto;
  padding: 10px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  margin-bottom: 10px;
  max-height: 300px;
}

.chat-widget .chat-message {
  margin-bottom: 12px;
  padding: 8px 12px;
  border-radius: 12px;
