  text-align: right;
}

.chat-widget .chat-message.user {
  background: rgba(255, 255, 255, 0.1);
  color: #fff;
}

.chat-widget .chat-message .message-header {
  font-size: 11px;
  opacity: 0.7;
  margin-bottom: 4px;
}

.chat-widget .chat-message .message-content {
  font-size: 14px;
  line-height: 1.4;
}

.chat-widget .chat-input-container {
  display: flex;
  gap: 8px;
  align-items: center;
}

.chat-widget #mainChatInput {
  flex: 1;
  padding: 10px 12px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  background: rgba(255, 255, 255, 0.1);
  color: #fff;
  font-size: 14px;
}

.chat-widget .chat-send-btn {
  width: 40px;
  height: 40px;
  border: none;
  border-radius: 50%;
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
}

.chat-widget .chat-send-btn:hover {
  transform: scale(1.1);
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
}

/* Media Player */
.media-player {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  pointer-events: none;
}

.media-player.video-mode {
  z-index: 1;
  pointer-events: auto;
}

.media-content {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

.media-content video {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.media-controls-overlay {
  position: absolute;
  bottom: 20px;
  right: 20px;
  z-index: 10;
}

.media-control-btn {
  width: 50px;
  height: 50px;
  border: none;
  border-radius: 50%;
